<div style="margin: 20px;">	<div><a href="#" onClick="$('.globals-subrow').show(); return false;">развернуть все</a></div>	<div><a href="#" onClick="$('.globals-subrow').hide(); return false;">свернуть все</a></div>	<? foreach($GLOBALS as $name => $arr): ?>			<? if($name=="GLOBALS")			continue;?>					<a href="#" class="globals-row" onclick="$(this).next().toggle(); return false;"><?= $name; ?></a>		<div class="globals-subrow"><? print_r($arr); ?></div>			<? endforeach; ?>			<div><a href="#" onClick="$('.globals-subrow').show(); return false;">развернуть все</a></div>	<div><a href="#" onClick="$('.globals-subrow').hide(); return false;">свернуть все</a></div></div>